# Acte 3 ‚Äî Diagrammes volume
if ("volume" %in% names(dt) && "datetime" %in% names(dt)) {
  dt_clean &lt;- dt %&gt;% filter(!is.na(volume), !is.na(datetime))

  # Time series volume
  p1 &lt;- ggplot(dt_clean, aes(x = datetime, y = volume)) +
    geom_line(color = "#1F78B4", linewidth = 0.3) +
    labs(title = "Volume horaire vs temps", x = "Datetime", y = "Volume") +
    theme_minimal()

  # Scatter volume vs |PRICE_CHANGE|
  if ("PRICE_CHANGE" %in% names(dt)) {
    dt_clean2 &lt;- dt_clean %&gt;% filter(!is.na(PRICE_CHANGE)) %&gt;%
      mutate(abs_change = abs(PRICE_CHANGE))
    p2 &lt;- ggplot(dt_clean2 %&gt;% sample_n(min(10000, nrow(dt_clean2))), 
                 aes(x = volume, y = abs_change)) +
      geom_point(alpha = 0.2, color = "#A6CEE3") +
      labs(title = "Volume vs |PRICE_CHANGE|", x = "Volume", y = "|Variation prix|") +
      theme_minimal()
  } else {
    p2 &lt;- NULL
  }

  # VOLUME_SMA overlay sur prix
  if (all(c("VOLUME_SMA","close") %in% names(dt))) {
    dt_clean3 &lt;- dt_clean %&gt;% filter(!is.na(VOLUME_SMA), !is.na(close))
    p3 &lt;- ggplot(dt_clean3, aes(x = datetime)) +
      geom_line(aes(y = close), color = "#2C7FB8", linewidth = 0.4) +
      geom_line(aes(y = VOLUME_SMA/max(VOLUME_SMA, na.rm = TRUE)*max(close, na.rm = TRUE)), 
                color = "#E31A1C", linewidth = 0.4, linetype = "dashed") +
      labs(title = "Prix (bleu) + VOLUME_SMA normalis√© (rouge pointill√©)", x = "Datetime", y = "Prix") +
      theme_minimal()
  } else {
    p3 &lt;- NULL
  }

  print(p1)
  if (!is.null(p2)) print(p2)
  if (!is.null(p3)) print(p3)
} else {
  cat("Volume ou datetime manquant.\n")
}

üé¨ Acte 7 ‚Äî Synth√®se finale
Message final: Bitcoin est un march√© hautement non-stationnaire, √©motionnel et volatil, mais riche en signaux exploitables.
R√©capitulatif: - Prix : croissance explosive avec phases de stagnation - Volatilit√© : constante mais fluctuante, le march√© m√ªrit lentement - Volume : corr√©l√© aux grands mouvements, indicateur de crises/bull runs - Momentum : march√© passe du temps en zones extr√™mes (RSI) - Tendances : courtes et violentes, alternance tendance/range - Ratios : le bruit domine √† l‚Äô√©chelle horaire ‚Üí importance du ML et risk management

# Acte 7 ‚Äî Tableau r√©capitulatif
summary_stats &lt;- data.frame(
  Dimension = c("Prix", "Volatilit√©", "Volume", "Momentum"),
  Indicateur_cl√© = c("CLOSE", "VOLATILITY_30D", "VOLUME", "RSI"),
  Observation = c(
    "Croissance non lin√©aire avec phases d'explosion et de stagnation",
    "Fluctuante mais constante, le march√© m√ªrit lentement",
    "Corr√©l√© aux grands mouvements de prix, indicateur de crises/bull runs",
    "Le march√© passe du temps en zones extr√™mes (surachat/survente)"
  )
)

if (requireNamespace("DT", quietly = TRUE)) {
  DT::datatable(summary_stats, options = list(pageLength = 10))
} else {
  print(summary_stats)
}

Data Preprocessing

# Helper function: find column by name variants (case-insensitive)
find_col &lt;- function(df, candidates) {
  cn_lower &lt;- tolower(names(df))
  idx &lt;- which(cn_lower %in% tolower(candidates))
  if (length(idx) == 0) return(NA_integer_)
  idx[1]
}

# Identify key columns
idx_time   &lt;- find_col(df, c("datetime","date","timestamp","time","open_time"))
idx_open   &lt;- find_col(df, c("open"))
idx_high   &lt;- find_col(df, c("high"))
idx_low    &lt;- find_col(df, c("low"))
idx_close  &lt;- find_col(df, c("close","close_price"))
idx_volume &lt;- find_col(df, c("volume","vol"))

# Create working dataset
dt &lt;- as_tibble(df)

# Parse datetime
if (!is.na(idx_time)) {
  time_raw &lt;- df[[idx_time]]
  if (!inherits(time_raw, "POSIXct")) {
    time_parsed &lt;- suppressWarnings(ymd_hms(time_raw, tz = "UTC"))
    if (all(is.na(time_parsed))) {
      time_parsed &lt;- suppressWarnings(ymd_hm(time_raw, tz = "UTC"))
    }
  } else {
    time_parsed &lt;- time_raw
  }
  dt &lt;- dt %&gt;% mutate(datetime = time_parsed)
}

# Standardize OHLCV columns
if (!is.na(idx_open))   dt &lt;- dt %&gt;% mutate(open = as.numeric(df[[idx_open]]))
if (!is.na(idx_high))   dt &lt;- dt %&gt;% mutate(high = as.numeric(df[[idx_high]]))
if (!is.na(idx_low))    dt &lt;- dt %&gt;% mutate(low = as.numeric(df[[idx_low]]))
if (!is.na(idx_close))  dt &lt;- dt %&gt;% mutate(close = as.numeric(df[[idx_close]]))
if (!is.na(idx_volume)) dt &lt;- dt %&gt;% mutate(volume = as.numeric(df[[idx_volume]]))

# Sort by datetime
if ("datetime" %in% names(dt)) dt &lt;- dt %&gt;% arrange(datetime)

cat("‚úÖ Preprocessing complete\n")

‚úÖ Preprocessing complete

Feature Engineering

# Calculate technical indicators
# Price changes (returns)
if ("close" %in% names(dt)) {
  dt &lt;- dt %&gt;% mutate(PRICE_CHANGE = close/lag(close) - 1)
}

# ATR (Average True Range) - volatility indicator
if (all(c("high","low","close") %in% names(dt))) {
  atr_result &lt;- TTR::ATR(cbind(dt$high, dt$low, dt$close), n = 14)
  dt$ATR &lt;- as.numeric(atr_result[,"atr"])
}

# RSI (Relative Strength Index) - momentum indicator
if ("close" %in% names(dt)) {
  dt$RSI &lt;- as.numeric(TTR::RSI(dt$close, n = 14))
}

# Moving averages
if ("close" %in% names(dt)) {
  dt$SMA_20 &lt;- as.numeric(TTR::SMA(dt$close, n = 20))
  dt$EMA_12 &lt;- as.numeric(TTR::EMA(dt$close, n = 12))
  dt$EMA_26 &lt;- as.numeric(TTR::EMA(dt$close, n = 26))
}

# MACD (Moving Average Convergence Divergence)
if ("close" %in% names(dt)) {
  macd &lt;- TTR::MACD(dt$close, nFast = 12, nSlow = 26, nSig = 9)
  dt$MACD &lt;- as.numeric(macd[,"macd"])
  dt$MACD_SIGNAL &lt;- as.numeric(macd[,"signal"])
}

# 30-day rolling volatility (standard deviation of returns)
if ("PRICE_CHANGE" %in% names(dt)) {
  dt$VOLATILITY_30D &lt;- as.numeric(
    zoo::rollapply(dt$PRICE_CHANGE, width = 720, FUN = sd, 
                   align = "right", fill = NA, na.rm = TRUE)
  )
}

# Volume moving average
if ("volume" %in% names(dt)) {
  dt$VOLUME_SMA &lt;- as.numeric(TTR::SMA(dt$volume, n = 20))
}

# Price ratios
if (all(c("high","low","close") %in% names(dt))) {
  dt$HIGH_LOW_RATIO &lt;- (dt$high - dt$low) / dt$close
}
if (all(c("close","open") %in% names(dt))) {
  dt$CLOSE_OPEN_RATIO &lt;- dt$close / dt$open - 1
}

cat("‚úÖ Feature engineering complete\n")

‚úÖ Feature engineering complete

cat("üìà Available indicators:", 
    paste(intersect(names(dt), 
                    c("PRICE_CHANGE","ATR","RSI","SMA_20","MACD","VOLATILITY_30D")),
          collapse = ", "), "\n")

